mapper.select:
SELECT cm.*,
ci.CONTRACT_VALUE AS DISCOUNT_PRICE,
cc.category_name, cid.id item_detail_id, cid.item_detail_content, ifnull(stock.stock_num,0) stock_num FROM cd_item cm left join cd_category cc on cm.cd_category_id = cc.id left join cd_item_detail cid on cid.cd_item_id = cm.id left join cd_stock stock on stock.item_id = cm.id
left join cd_contract c on c.CD_COMPANY_ID= ${companyId} and c.IS_DEL =0 left join cd_contract_item ci on ci.CD_ITEM_ID=cm.ID and ci.CD_CONTRACT_ID=c.id
AND (cm.item_Code LIKE CONCAT('%', #{keyword}, '%') or cm.item_name LIKE CONCAT('%', #{keyword}, '%') or cc.CATEGORY_NAME LIKE CONCAT('%',#{keyword}, '%'))
AND cm.id = #{id}
AND cm.id NOT IN
#{listItem}
AND cm.id IN
#{listItem}
AND cm.item_code IN
#{listItem}
AND cm.sell_status = #{sellStatus}
AND cm.item_type = #{itemType}
AND cm.sell_unit = #{sellUnit}
order by cm.item_code
